import gql from "graphql-tag"

const ARTICLES_BY_TYPE_QUERY = gql`
  query ArticlesByType($type: String!) {
    articles {
      id
      title
      types(where: {type: $type}) {
        type
      }
    }
  }
`

export default ARTICLES_BY_TYPE_QUERY
