import Article from "../../../components/article"

const ArticleType = () => {
	const router = useRouter()

	return (
			<QueryType query={ARTICLES_BY_TYPE_QUERY} type={router.query.type}>
				{({ data: { articles } }) => (
					articles.map(article =>
