import React from "react";
import Link from "next/link";

const Card = ({ article }) => {
	const {id, types, title} = article

	return (
		<Link as={`/article/${id}`} href={{ pathname: "article", query: { id: id } }}>
			<>
				<p>
					{types.map(type =>
						<strong>{type.type}</strong>
					)}
				</p>
				<p>
					{title}
				</p>
			</>
		</Link>
	);
};

export default Card;
