import styled from "styled-components"

export const HeaderStyled = styled.header`
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  padding:40px 0;
  background:#cecece;
`

export const LeftNavStyled = styled.div`
	position: absolute;
	top:10px;
	left:10px;
`

export const RightNavStyled = styled.div`
	position: absolute;
	top:10px;
	right:10px;
`

export const BlogLink = styled.a`
	text-transform:uppercase;
	font-size:12px;
	padding:0 5px;
	position:relative;

	&:after {
		content: '|'
		position:absolute;
		right:-5px;
	}

	&:last-child:after {
		content: ''
	}
`
