import styled from "styled-components"

export const Article = styled.header`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
  width:100%;
  padding:40px 0;
  border: 1px solid ${props => props.size === 'size-large' ? 'black' : 'none'};
`
