import { useRouter } from 'next/router'
import QueryType from '../../../components/utils/query_type'
import ReactMarkdown from 'react-markdown'
import ARTICLES_BY_TYPE_QUERY from '../../../apollo/queries/article-by-type'
import Moment from 'react-moment'
import {ArticlePage, Header, Content} from '../../../components/article_styled.js'

const Article = () => {
	const router = useRouter()

	return (
			<QueryType query={ARTICLES_BY_TYPE_QUERY} type={router.query.type}>
				{({ data: { articles } }) => (
					articles.map(article =>
							<ArticlePage>
								<Header>
									{article.title}
									<small>
										<Moment format="Do MMM YYYY">{article.published_at}</Moment>
									</small>
								</Header>
								<Content>
									<ReactMarkdown source={article.content} />
								</Content>
							</ArticlePage>
					)
				)}
			</QueryType>
	)
}

export default Article
